import React, { useMemo, useState } from 'react';

/**
 * LegacyLayout.jsx
 * - Keeps ALL existing DOM ids/classes so legacy JS continues to work.
 * - Adds a small "Go to coordinates" (lat/lon) panel without touching existing behavior.
 */
export default function LegacyLayout() {
  const [latText, setLatText] = useState('');
  const [lonText, setLonText] = useState('');
  const [coordError, setCoordError] = useState('');

  const getLegacyMap = () => {
    // Try common globals used by legacy Mapbox setups (no breaking assumptions).
    return (
      window.map ||
      window.legacyMap ||
      window.__legacyMap ||
      window.__map ||
      window._map ||
      null
    );
  };

  const parseCoord = (s) => {
    if (typeof s !== 'string') return NaN;
    // allow commas as decimal separators
    const n = Number(s.trim().replace(',', '.'));
    return Number.isFinite(n) ? n : NaN;
  };

  const validateLatLon = (lat, lon) => {
    if (!Number.isFinite(lat) || !Number.isFinite(lon)) {
      return 'Please enter valid numeric latitude and longitude.';
    }
    if (lat < -90 || lat > 90) return 'Latitude must be between -90 and 90.';
    if (lon < -180 || lon > 180) return 'Longitude must be between -180 and 180.';
    return '';
  };

  const flyToLatLon = (lat, lon) => {
    const map = getLegacyMap();
    if (!map) {
      setCoordError(
        'Map instance not found on window (expected window.map / window.legacyMap).'
      );
      return;
    }

    // Preserve current zoom if user already zoomed in; otherwise use a sensible default.
    const currentZoom =
      typeof map.getZoom === 'function' ? Number(map.getZoom()) : NaN;
    const targetZoom = Number.isFinite(currentZoom)
      ? Math.max(currentZoom, 14)
      : 14;

    // Mapbox GL: flyTo is preferred, fall back to jumpTo.
    const center = [lon, lat];

    try {
      if (typeof map.flyTo === 'function') {
        map.flyTo({ center, zoom: targetZoom, essential: true });
      } else if (typeof map.jumpTo === 'function') {
        map.jumpTo({ center, zoom: targetZoom });
      } else if (typeof map.setCenter === 'function') {
        map.setCenter(center);
        if (typeof map.setZoom === 'function') map.setZoom(targetZoom);
      }
    } catch (e) {
      setCoordError(`Could not move map: ${String(e?.message || e)}`);
      return;
    }

    // Optional marker (only if mapboxgl exists). This does not affect existing logic.
    try {
      const mb = window.mapboxgl;
      if (mb && typeof mb.Marker === 'function') {
        if (window.__legacyCoordMarker) {
          window.__legacyCoordMarker.setLngLat(center);
        } else {
          window.__legacyCoordMarker = new mb.Marker({ color: '#111' })
            .setLngLat(center)
            .addTo(map);
        }
      }
    } catch {
      // ignore marker failures silently
    }
  };

  const onGoToCoords = () => {
    const lat = parseCoord(latText);
    const lon = parseCoord(lonText);
    const err = validateLatLon(lat, lon);
    setCoordError(err);
    if (err) return;
    flyToLatLon(lat, lon);
  };

  const onUseMapCenter = () => {
    const map = getLegacyMap();
    if (!map || typeof map.getCenter !== 'function') {
      setCoordError('Map center is not available.');
      return;
    }
    const c = map.getCenter();
    const lat = Number(c?.lat);
    const lon = Number(c?.lng);
    if (!Number.isFinite(lat) || !Number.isFinite(lon)) {
      setCoordError('Map center is not available.');
      return;
    }
    setCoordError('');
    setLatText(String(lat.toFixed(6)));
    setLonText(String(lon.toFixed(6)));
  };

  const coordHelp = useMemo(
    () => 'Example: lat 35.1856, lon 33.3823',
    []
  );

  return (
    <div className="legacy-app">
      {/* LEFT SIDEBAR */}
      <div className="sidebar">
        <div className="sidebar-header">
          <h2>Legacy Index Viewer</h2>
        </div>

        {/* Existing legacy controls (do NOT change ids/classes) */}
        <div className="controls">
          <label>Index</label>
          <select id="indexSelect" defaultValue="BAIS2">
            <option value="BAIS2">BAIS2</option>
            <option value="BAI">BAI</option>
            <option value="NDVI">NDVI</option>
            <option value="NDMI">NDMI</option>
            <option value="NDWI">NDWI</option>
          </select>

          <label>Date</label>
          <input id="dateSelect" type="date" />

          <button id="renderButton">Render</button>
          <button id="downloadButton">Download</button>
          <button id="clearButton">Clear</button>

          {/* âœ… NEW: Coordinate input (non-breaking, standalone) */}
          <div style={{ marginTop: 14, paddingTop: 12, borderTop: '1px solid rgba(0,0,0,0.08)' }}>
            <div style={{ fontWeight: 600, marginBottom: 8 }}>Go to coordinates</div>

            <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: 8 }}>
              <div style={{ display: 'flex', flexDirection: 'column', gap: 4 }}>
                <label style={{ margin: 0, fontSize: 12, opacity: 0.8 }}>Latitude</label>
                <input
                  type="text"
                  inputMode="decimal"
                  placeholder="e.g., 35.1856"
                  value={latText}
                  onChange={(e) => setLatText(e.target.value)}
                  onKeyDown={(e) => {
                    if (e.key === 'Enter') onGoToCoords();
                  }}
                />
              </div>

              <div style={{ display: 'flex', flexDirection: 'column', gap: 4 }}>
                <label style={{ margin: 0, fontSize: 12, opacity: 0.8 }}>Longitude</label>
                <input
                  type="text"
                  inputMode="decimal"
                  placeholder="e.g., 33.3823"
                  value={lonText}
                  onChange={(e) => setLonText(e.target.value)}
                  onKeyDown={(e) => {
                    if (e.key === 'Enter') onGoToCoords();
                  }}
                />
              </div>
            </div>

            <div style={{ display: 'flex', gap: 8, marginTop: 8 }}>
              <button type="button" onClick={onGoToCoords} style={{ flex: 1 }}>
                Go
              </button>
              <button type="button" onClick={onUseMapCenter} style={{ flex: 1 }}>
                Use map center
              </button>
            </div>

            <div style={{ fontSize: 12, opacity: 0.75, marginTop: 6 }}>
              {coordHelp}
            </div>

            {coordError ? (
              <div style={{ marginTop: 8, fontSize: 12, color: '#b00020' }}>
                {coordError}
              </div>
            ) : null}
          </div>
        </div>
      </div>

      {/* MAP CONTAINER (legacy ids preserved) */}
      <div className="map-wrap">
        <div id="map" />
      </div>

      {/* LEGACY MODALS (unchanged) */}
      <div id="graphModal" className="modal" style={{ display: 'none' }}>
        <div className="modal-content">
          <div className="modal-header">
            <span>Graph</span>
            <span
              className="close"
              onClick={() => window.closeGraphModal && window.closeGraphModal()}
            >
              &times;
            </span>
          </div>
          <iframe id="graphFrame" title="graph" style={{ border: 0 }} />
        </div>
      </div>

      <div id="weatherModal" className="modal" style={{ display: 'none' }}>
        <div className="modal-content">
          <div className="modal-header">
            <span>Weather</span>
            <span
              className="close"
              onClick={() =>
                window.closeWeatherModal && window.closeWeatherModal()
              }
            >
              &times;
            </span>
          </div>
          <iframe id="weatherFrame" title="weather" style={{ border: 0 }} />
        </div>
      </div>

      <div id="infoModal" className="modal" style={{ display: 'none' }}>
        <div className="modal-content">
          <div className="modal-header">
            <span>About</span>
            <span
              className="close"
              onClick={() => window.closeInfoModal && window.closeInfoModal()}
            >
              &times;
            </span>
          </div>
          <div className="modal-body">
            <p>This modal shows information about the graphs.</p>
          </div>
        </div>
      </div>
    </div>
  );
}
